package ch.bbw.zork;import java.util.Arrays;import java.util.List;public class CommandWords {	private final List<String> validCommands = Arrays.asList("go", "quit", "help" );	public boolean isCommand(String commandWord) {		return validCommands.stream().anyMatch(c -> c.equals(commandWord));	}	public String showAll() {		return String.join(" ", validCommands);	}}